import { createStore } from "redux";

const startingState = {
  balance: 0,
  loan: 0,
  loanPurpose: "",
};

function reducer(state = startingState, action) {
  switch (action.type) {
    case "accounts/deposit":
      return { ...state, balance: state.balance + action.payload };
    case "accounts/withdraw":
      return { ...state, balance: state.balance - action.payload };
      case "accounts/requestLoan":
        if(state.loan >0) return;
        return {...state, loan :action.payload.amount, loanPurpose:action.payload }

    default:
      return state;
  }
}

export const store = createStore(reducer);
store.dispatch({ type: "accounts/deposit", payload: 500 });
console.log(store.getState());
store.dispatch({ type: "accounts/withdraw", payload: 500 });
console.log(store.getState());
store.dispatch({type:"accounts/requestLoan", payload:{
    amount:1000,
    purpose:"To buy a car"
}})
